            'Accept' =&gt; 'application/json',
            'Accept-Language' =&gt; 'en',
        ],
        'json' =&gt; [
            'name' =&gt; 'emohk',
            'password' =&gt; 'emohk',
            'remember' =&gt; null,
        ],
    ]
);
$body = $response-&gt;getBody();
